import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertApplicationSchema, updateApplicationSchema, insertSessionSchema } from "@shared/schema";
import { generateInterviewQuestions, provideFeedback } from "./services/gemini";

export async function registerRoutes(app: Express): Promise<Server> {
  // Application CRUD routes
  app.get("/api/applications", async (req, res) => {
    try {
      const { status, tag, search, page = "1", limit = "10" } = req.query;
      const offset = (parseInt(page as string) - 1) * parseInt(limit as string);

      const result = await storage.getApplications({
        status: status as string,
        tag: tag as string,
        search: search as string,
        limit: parseInt(limit as string),
        offset,
      });

      res.json({
        applications: result.applications,
        total: result.total,
        page: parseInt(page as string),
        totalPages: Math.ceil(result.total / parseInt(limit as string)),
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch applications" });
    }
  });

  app.get("/api/applications/:id", async (req, res) => {
    try {
      const application = await storage.getApplication(req.params.id);
      if (!application) {
        return res.status(404).json({ message: "Application not found" });
      }

      // Get interview sessions for this application
      const sessions = await storage.getSessionsByApplication(req.params.id);

      res.json({ ...application, sessions });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch application" });
    }
  });

  app.post("/api/applications", async (req, res) => {
    try {
      const validatedData = insertApplicationSchema.parse(req.body);
      const application = await storage.createApplication(validatedData);
      res.status(201).json(application);
    } catch (error) {
      res.status(400).json({ message: "Invalid application data" });
    }
  });

  app.patch("/api/applications/:id", async (req, res) => {
    try {
      const validatedData = updateApplicationSchema.parse({
        ...req.body,
        id: req.params.id,
      });

      const application = await storage.updateApplication(validatedData);
      if (!application) {
        return res.status(404).json({ message: "Application not found" });
      }

      res.json(application);
    } catch (error) {
      res.status(400).json({ message: "Invalid application data" });
    }
  });

  app.delete("/api/applications/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteApplication(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Application not found" });
      }

      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete application" });
    }
  });

  // Statistics route
  app.get("/api/stats", async (req, res) => {
    try {
      const stats = await storage.getApplicationStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch statistics" });
    }
  });

  // AI Interview routes
  app.post("/api/ai/questions", async (req, res) => {
    try {
      const { role, company } = req.body;
      if (!role) {
        return res.status(400).json({ message: "Role is required" });
      }

      const questions = await generateInterviewQuestions(role, company);
      res.json({ questions });
    } catch (error) {
      console.error("Failed to generate questions:", error);
      res.status(500).json({ message: "Failed to generate interview questions" });
    }
  });

  app.post("/api/ai/feedback", async (req, res) => {
    try {
      const { question, answer, role } = req.body;
      if (!question || !answer) {
        return res.status(400).json({ message: "Question and answer are required" });
      }

      const feedback = await provideFeedback(question, answer, role);
      res.json(feedback);
    } catch (error) {
      console.error("Failed to provide feedback:", error);
      res.status(500).json({ message: "Failed to provide feedback" });
    }
  });

  // Interview sessions
  app.post("/api/sessions", async (req, res) => {
    try {
      const validatedData = insertSessionSchema.parse(req.body);
      const session = await storage.createSession(validatedData);
      res.status(201).json(session);
    } catch (error) {
      res.status(400).json({ message: "Invalid session data" });
    }
  });

  app.get("/api/sessions/:applicationId", async (req, res) => {
    try {
      const sessions = await storage.getSessionsByApplication(req.params.applicationId);
      res.json(sessions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch sessions" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type Application, 
  type InsertApplication,
  type UpdateApplication,
  type InterviewSession,
  type InsertSession,
  type QuestionWithAnswer 
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Application CRUD
  getApplication(id: string): Promise<Application | undefined>;
  getApplications(filters?: {
    status?: string;
    tag?: string;
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<{ applications: Application[]; total: number }>;
  createApplication(application: InsertApplication): Promise<Application>;
  updateApplication(application: UpdateApplication): Promise<Application | undefined>;
  deleteApplication(id: string): Promise<boolean>;

  // Interview Sessions
  getSessionsByApplication(applicationId: string): Promise<InterviewSession[]>;
  createSession(session: InsertSession): Promise<InterviewSession>;

  // Statistics
  getApplicationStats(): Promise<{
    total: number;
    interviews: number;
    offers: number;
    responseRate: number;
  }>;
}

export class MemStorage implements IStorage {
  private applications: Map<string, Application>;
  private sessions: Map<string, InterviewSession>;

  constructor() {
    this.applications = new Map();
    this.sessions = new Map();
  }

  async getApplication(id: string): Promise<Application | undefined> {
    return this.applications.get(id);
  }

  async getApplications(filters?: {
    status?: string;
    tag?: string;
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<{ applications: Application[]; total: number }> {
    let apps = Array.from(this.applications.values());

    // Apply filters
    if (filters?.status && filters.status !== 'All') {
      apps = apps.filter(app => app.status.toLowerCase() === filters.status?.toLowerCase());
    }

    if (filters?.tag && filters.tag !== 'All') {
      apps = apps.filter(app => app.tag.toLowerCase() === filters.tag?.toLowerCase());
    }

    if (filters?.search) {
      const search = filters.search.toLowerCase();
      apps = apps.filter(app => 
        app.company.toLowerCase().includes(search) ||
        app.role.toLowerCase().includes(search)
      );
    }

    // Sort by creation date (newest first)
    apps.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());

    const total = apps.length;

    // Apply pagination
    const limit = filters?.limit || 10;
    const offset = filters?.offset || 0;
    const paginatedApps = apps.slice(offset, offset + limit);

    return { applications: paginatedApps, total };
  }

  async createApplication(insertApp: InsertApplication): Promise<Application> {
    const id = randomUUID();
    const now = new Date();
    const application: Application = {
      ...insertApp,
      id,
      jobUrl: insertApp.jobUrl || null,
      notes: insertApp.notes || null,
      interviewNotes: [],
      createdAt: now,
      updatedAt: now,
    };
    this.applications.set(id, application);
    return application;
  }

  async updateApplication(updateApp: UpdateApplication): Promise<Application | undefined> {
    const existing = this.applications.get(updateApp.id);
    if (!existing) return undefined;

    const updated: Application = {
      ...existing,
      ...updateApp,
      updatedAt: new Date(),
    };
    this.applications.set(updateApp.id, updated);
    return updated;
  }

  async deleteApplication(id: string): Promise<boolean> {
    return this.applications.delete(id);
  }

  async getSessionsByApplication(applicationId: string): Promise<InterviewSession[]> {
    return Array.from(this.sessions.values())
      .filter(session => session.applicationId === applicationId)
      .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
  }

  async createSession(insertSession: InsertSession): Promise<InterviewSession> {
    const id = randomUUID();
    const session: InterviewSession = {
      ...insertSession,
      id,
      createdAt: new Date(),
    };
    this.sessions.set(id, session);
    return session;
  }

  async getApplicationStats(): Promise<{
    total: number;
    interviews: number;
    offers: number;
    responseRate: number;
  }> {
    const apps = Array.from(this.applications.values());
    const total = apps.length;
    const interviews = apps.filter(app => app.status === 'interview').length;
    const offers = apps.filter(app => app.status === 'offer').length;
    const applied = apps.filter(app => app.status === 'applied').length;
    const responseRate = applied > 0 ? Math.round(((interviews + offers) / applied) * 100) : 0;

    return { total, interviews, offers, responseRate };
  }
}

export const storage = new MemStorage();

