import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import mongoose, { Schema, Document } from "mongoose";
import { z } from "zod";

// MongoDB Schemas
const applicationSchema = new Schema({
  company: { type: String, required: true },
  role: { type: String, required: true },
  status: { type: String, required: true }, // applied, interview, offer, rejected
  tag: { type: String, required: true }, // dream, target, backup
  jobUrl: { type: String, default: "" },
  notes: { type: String, default: "" },
  interviewNotes: { type: [Schema.Types.Mixed], default: [] },
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
});

const interviewSessionSchema = new Schema({
  applicationId: { type: String, required: true },
  questions: { type: [Schema.Types.Mixed], required: true }, // Array of questions with answers and feedback
  createdAt: { type: Date, default: Date.now },
});

// MongoDB Models
export const Application = mongoose.model("Application", applicationSchema);
export const InterviewSession = mongoose.model("InterviewSession", interviewSessionSchema);

// Zod validation schemas for API
export const insertApplicationSchema = z.object({
  company: z.string().min(1, "Company name is required"),
  role: z.string().min(1, "Role is required"),
  status: z.enum(["applied", "interview", "offer", "rejected"]),
  tag: z.enum(["dream", "target", "backup"]),
  jobUrl: z.string().optional(),
  notes: z.string().optional(),
});

export const updateApplicationSchema = insertApplicationSchema.partial().extend({
  id: z.string(),
});

export const insertSessionSchema = z.object({
  applicationId: z.string().min(1, "Application ID is required"),
  questions: z.array(z.any()),
});

// TypeScript types
export type InsertApplication = z.infer<typeof insertApplicationSchema>;
export type UpdateApplication = z.infer<typeof updateApplicationSchema>;
export type ApplicationType = {
  _id: string;
  company: string;
  role: string;
  status: "applied" | "interview" | "offer" | "rejected";
  tag: "dream" | "target" | "backup";
  jobUrl?: string;
  notes?: string;
  interviewNotes: any[];
  createdAt: Date;
  updatedAt: Date;
};
export type InterviewSessionType = {
  _id: string;
  applicationId: string;
  questions: any[];
  createdAt: Date;
};
export type InsertSession = z.infer<typeof insertSessionSchema>;

// Types for interview chat
export interface ChatMessage {
  id: string;
  type: "question" | "answer" | "feedback";
  content: string;
  timestamp: Date;
  feedback?: InterviewFeedback;
}

export interface InterviewFeedback {
  clarity: number; // 1-5 scale
  relevance: number; // 1-5 scale
  suggestions: string[];
  overall: string;
}

export interface QuestionWithAnswer {
  question: string;
  answer: string;
  feedback: InterviewFeedback;
  timestamp: Date;
}

