import { Link } from "wouter";
import type { Application } from "@shared/schema";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { MoreVertical, Zap, Eye, Trash2, Edit } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

interface ApplicationCardProps {
  application: Application;
  onEdit?: (application: Application) => void;
}

export default function ApplicationCard({ application, onEdit }: ApplicationCardProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const deleteMutation = useMutation({
    mutationFn: api.deleteApplication,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/applications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/stats'] });
      toast({
        title: "Success",
        description: "Application deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete application",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'applied':
        return 'bg-blue-100 text-blue-800';
      case 'interview':
        return 'bg-amber-100 text-amber-800';
      case 'offer':
        return 'bg-green-100 text-green-800';
      case 'rejected':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getTagColor = (tag: string) => {
    switch (tag) {
      case 'dream':
        return 'bg-purple-100 text-purple-800';
      case 'target':
        return 'bg-green-100 text-green-800';
      case 'backup':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const practiceCount = Array.isArray(application.interviewNotes) ? application.interviewNotes.length : 0;
  const notesCount = application.notes ? 1 : 0;

  return (
    <div 
      className="p-6 hover:bg-accent/50 transition-colors border-b border-border last:border-b-0" 
      data-testid={`card-application-${application.id}`}
    >
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <div className="flex items-center space-x-3 mb-2">
            <Link href={`/application/${application.id}`}>
              <a className="text-lg font-semibold text-foreground hover:text-primary transition-colors" data-testid={`link-application-${application.id}`}>
                {application.role}
              </a>
            </Link>
            <Badge className={getStatusColor(application.status)} data-testid={`badge-status-${application.status}`}>
              {application.status}
            </Badge>
            <Badge className={getTagColor(application.tag)} data-testid={`badge-tag-${application.tag}`}>
              {application.tag}
            </Badge>
          </div>

          <p className="text-muted-foreground font-medium" data-testid={`text-company-${application.id}`}>
            {application.company}
          </p>

          <p className="text-sm text-muted-foreground mt-1" data-testid={`text-applied-${application.id}`}>
            Applied {formatDistanceToNow(new Date(application.createdAt), { addSuffix: true })}
          </p>

          <div className="flex items-center space-x-4 mt-3 text-sm text-muted-foreground">
            <span className="flex items-center" data-testid={`text-practice-count-${application.id}`}>
              <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"/>
              </svg>
              {practiceCount} practice session{practiceCount !== 1 ? 's' : ''}
            </span>
            {notesCount > 0 && (
              <span className="flex items-center" data-testid={`text-notes-count-${application.id}`}>
                <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"/>
                </svg>
                {notesCount} note{notesCount !== 1 ? 's' : ''}
              </span>
            )}
          </div>
        </div>

        <div className="flex items-center space-x-2">
          <Link href={`/ai-prep/${application.id}`}>
            <Button
              variant="ghost"
              size="icon"
              className="text-muted-foreground hover:text-primary"
              title="Start AI Prep"
              data-testid={`button-ai-prep-${application.id}`}
            >
              <Zap className="w-4 h-4" />
            </Button>
          </Link>

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                className="text-muted-foreground hover:text-foreground"
                data-testid={`button-menu-${application.id}`}
              >
                <MoreVertical className="w-4 h-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <Link href={`/application/${application.id}`}>
                <DropdownMenuItem data-testid={`menu-view-${application.id}`}>
                  <Eye className="w-4 h-4 mr-2" />
                  View Details
                </DropdownMenuItem>
              </Link>
              {onEdit && (
                <DropdownMenuItem 
                  onClick={() => onEdit(application)}
                  data-testid={`menu-edit-${application.id}`}
                >
                  <Edit className="w-4 h-4 mr-2" />
                  Edit
                </DropdownMenuItem>
              )}
              <DropdownMenuItem
                onClick={() => deleteMutation.mutate(application.id)}
                className="text-destructive"
                data-testid={`menu-delete-${application.id}`}
              >
                <Trash2 className="w-4 h-4 mr-2" />
                Delete
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </div>
  );
}

client/src/components/application-form.t.content:
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertApplicationSchema, type InsertApplication } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

interface ApplicationFormProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function ApplicationForm({ open, onOpenChange }: ApplicationFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertApplication>({
    resolver: zodResolver(insertApplicationSchema),
    defaultValues: {
      company: "",
      role: "",
      status: "applied",
      tag: "target",
      jobUrl: "",
      notes: "",
    },
  });

  const createMutation = useMutation({
    mutationFn: api.createApplication,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/applications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/stats'] });
      toast({
        title: "Success",
        description: "Application added successfully",
      });
      form.reset();
      onOpenChange(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add application",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertApplication) => {
    createMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md" data-testid="dialog-add-application">
        <DialogHeader>
          <DialogTitle>Add New Application</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="company"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Company Name</FormLabel>
                  <FormControl>
                    <Input placeholder="e.g., Google" {...field} data-testid="input-company" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="role"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Job Title</FormLabel>
                  <FormControl>
                    <Input placeholder="e.g., Senior Frontend Developer" {...field} data-testid="input-role" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Status</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-status">
                          <SelectValue placeholder="Select status" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="applied">Applied</SelectItem>
                        <SelectItem value="interview">Interview</SelectItem>
                        <SelectItem value="offer">Offer</SelectItem>
                        <SelectItem value="rejected">Rejected</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="tag"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Priority</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-tag">
                          <SelectValue placeholder="Select priority" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="dream">Dream</SelectItem>
                        <SelectItem value="target">Target</SelectItem>
                        <SelectItem value="backup">Backup</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="jobUrl"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Job Description URL</FormLabel>
                  <FormControl>
                    <Input type="url" placeholder="https://..." {...field} value={field.value || ""} data-testid="input-job-url" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Initial Notes</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Add any initial thoughts or requirements..."
                      className="h-20"
                      {...field}
                      value={field.value || ""}
                      data-testid="textarea-notes"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <DialogFooter className="gap-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createMutation.isPending}
                data-testid="button-submit"
              >
                {createMutation.isPending ? "Adding..." : "Add Application"}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/chat-interface.tsx.content:
import { useState, useRef, useEffect } from "react";
import { useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { Send, Loader2, Zap, CheckCircle2 } from "lucide-react";
import type { ChatMessage, InterviewFeedback, QuestionWithAnswer } from "@shared/schema";

interface ChatInterfaceProps {
  applicationId?: string;
  role: string;
  company?: string;
  onSessionComplete?: (questions: QuestionWithAnswer[]) => void;
}

export default function ChatInterface({ applicationId, role, company, onSessionComplete }: ChatInterfaceProps) {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [input, setInput] = useState("");
  const [currentQuestion, setCurrentQuestion] = useState<string | null>(null);
  const [sessionQuestions, setSessionQuestions] = useState<QuestionWithAnswer[]>([]);
  const scrollAreaRef = useRef<HTMLDivElement>(null);

  const generateQuestionMutation = useMutation({
    mutationFn: () => api.generateQuestions(role, company),
    onSuccess: (data) => {
      if (data.questions.length > 0) {
        const question = data.questions[0];
        setCurrentQuestion(question);

        const newMessage: ChatMessage = {
          id: Date.now().toString(),
          type: "question",
          content: question,
          timestamp: new Date(),
        };

        setMessages(prev => [...prev, newMessage]);
      }
    },
  });

  const feedbackMutation = useMutation({
    mutationFn: ({ question, answer }: { question: string; answer: string }) => 
      api.getFeedback(question, answer, role),
    onSuccess: (feedback: InterviewFeedback, variables) => {
      // Add feedback message
      const feedbackMessage: ChatMessage = {
        id: Date.now().toString(),
        type: "feedback",
        content: feedback.overall,
        timestamp: new Date(),
        feedback,
      };

      setMessages(prev => [...prev, feedbackMessage]);

      // Save to session
      if (currentQuestion) {
        const questionWithAnswer: QuestionWithAnswer = {
          question: currentQuestion,
          answer: variables.answer,
          feedback,
          timestamp: new Date(),
        };

        setSessionQuestions(prev => [...prev, questionWithAnswer]);
      }

      // Clear current question
      setCurrentQuestion(null);
    },
  });

  const saveSessionMutation = useMutation({
    mutationFn: () => {
      if (!applicationId) throw new Error("No application ID");
      return api.createSession(applicationId, sessionQuestions);
    },
    onSuccess: () => {
      onSessionComplete?.(sessionQuestions);
    },
  });

  useEffect(() => {
    if (scrollAreaRef.current) {
      scrollAreaRef.current.scrollTop = scrollAreaRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    if (currentQuestion) {
      // This is an answer to a question
      const answerMessage: ChatMessage = {
        id: Date.now().toString(),
        type: "answer",
        content: input,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, answerMessage]);
      feedbackMutation.mutate({ question: currentQuestion, answer: input });
    }

    setInput("");
  };

  const startNewQuestion = () => {
    generateQuestionMutation.mutate();
  };

  const endSession = () => {
    if (sessionQuestions.length > 0 && applicationId) {
      saveSessionMutation.mutate();
    } else {
      onSessionComplete?.(sessionQuestions);
    }
  };

  const getFeedbackColor = (rating: number) => {
    if (rating >= 4) return "text-green-600 bg-green-50 border-green-200";
    if (rating >= 3) return "text-yellow-600 bg-yellow-50 border-yellow-200";
    return "text-red-600 bg-red-50 border-red-200";
  };

  return (
    <div className="bg-card rounded-lg border border-border shadow-sm h-fit">
      <div className="p-6 border-b border-border">
        <div className="flex items-center space-x-3 mb-4">
          <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-lg flex items-center justify-center">
            <Zap className="w-4 h-4 text-white" />
          </div>
          <h3 className="text-lg font-semibold text-foreground">AI Interview Prep</h3>
        </div>
        <p className="text-sm text-muted-foreground mb-4">
          Practice with AI-generated questions tailored to {role} {company ? `at ${company}` : 'role'}.
        </p>

        {messages.length === 0 && (
          <Button
            onClick={startNewQuestion}
            disabled={generateQuestionMutation.isPending}
            className="w-full bg-gradient-to-r from-purple-500 to-blue-500 text-white rounded-lg hover:shadow-lg transition-all duration-200"
            data-testid="button-start-practice"
          >
            {generateQuestionMutation.isPending ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Generating Question...
              </>
            ) : (
              "Start Practice Session"
            )}
          </Button>
        )}
      </div>

      {messages.length > 0 && (
        <>
          <ScrollArea className="p-4 bg-muted/30 max-h-96" ref={scrollAreaRef} data-testid="chat-messages">
            <div className="space-y-4">
              {messages.map((message) => (
                <div key={message.id} className={`flex items-start space-x-3 ${
                  message.type === 'answer' ? 'justify-end' : ''
                }`}>
                  {message.type !== 'answer' && (
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                      message.type === 'question' 
                        ? 'bg-gradient-to-r from-purple-500 to-blue-500' 
                        : 'bg-green-500'
                    }`}>
                      {message.type === 'question' ? (
                        <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z"/>
                        </svg>
                      ) : (
                        <CheckCircle2 className="w-4 h-4 text-white" />
                      )}
                    </div>
                  )}

                  <div className={`max-w-xs ${
                    message.type === 'answer'
                      ? 'bg-primary text-primary-foreground rounded-lg p-3'
                      : message.type === 'feedback'
                      ? `border rounded-lg p-3 ${getFeedbackColor(message.feedback?.clarity || 1)}`
                      : 'bg-white rounded-lg p-3 shadow-sm border border-border'
                  }`}>
                    <p className="text-sm" data-testid={`message-${message.type}-${message.id}`}>
                      {message.content}
                    </p>

                    {message.feedback && (
                      <div className="space-y-2 mt-3 text-xs">
                        <div className="flex items-center justify-between">
                          <span className="font-medium">Clarity:</span>
                          <div className="flex space-x-1">
                            {[1, 2, 3, 4, 5].map((rating) => (
                              <div
                                key={rating}
                                className={`w-2 h-2 rounded-full ${
                                  rating <= message.feedback!.clarity ? 'bg-current' : 'bg-current opacity-20'
                                }`}
                              />
                            ))}
                          </div>
                        </div>
                        <div className="flex items-center justify-between">
                          <span className="font-medium">Relevance:</span>
                          <div className="flex space-x-1">
                            {[1, 2, 3, 4, 5].map((rating) => (
                              <div
                                key={rating}
                                className={`w-2 h-2 rounded-full ${
                                  rating <= message.feedback!.relevance ? 'bg-current' : 'bg-current opacity-20'
                                }`}
                              />
                            ))}
                          </div>
                        </div>
                        {message.feedback.suggestions.length > 0 && (
                          <div>
                            <span className="font-medium block mb-1">Tips:</span>
                            <ul className="list-disc list-inside space-y-1">
                              {message.feedback.suggestions.map((tip, index) => (
                                <li key={index}>{tip}</li>
                              ))}
                            </ul>
                          </div>
                        )}
                      </div>
                    )}
                  </div>

                  {message.type === 'answer' && (
                    <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center flex-shrink-0 text-primary-foreground text-xs font-medium">
                      JD
                    </div>
                  )}
                </div>
              ))}

              {feedbackMutation.isPending && (
                <div className="flex items-start space-x-3">
                  <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center flex-shrink-0">
                    <Loader2 className="w-4 h-4 text-white animate-spin" />
                  </div>
                  <div className="bg-white rounded-lg p-3 shadow-sm border border-border">
                    <p className="text-sm text-muted-foreground">Analyzing your answer...</p>
                  </div>
                </div>
              )}
            </div>
          </ScrollArea>

          <div className="p-4 border-t border-border space-y-3">
            {!currentQuestion && !feedbackMutation.isPending && (
              <div className="flex space-x-2">
                <Button
                  onClick={startNewQuestion}
                  disabled={generateQuestionMutation.isPending}
                  className="flex-1"
                  variant="outline"
                  data-testid="button-next-question"
                >
                  {generateQuestionMutation.isPending ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Generating...
                    </>
                  ) : (
                    "Next Question"
                  )}
                </Button>

                {sessionQuestions.length > 0 && (
                  <Button
                    onClick={endSession}
                    disabled={saveSessionMutation.isPending}
                    data-testid="button-end-session"
                  >
                    {saveSessionMutation.isPending ? (
                      <>
                        <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                        Saving...
                      </>
                    ) : (
                      "End Session"
                    )}
                  </Button>
                )}
              </div>
            )}

            {currentQuestion && (
              <form onSubmit={handleSubmit} className="flex items-center space-x-2">
                <Input
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="Type your answer..."
                  disabled={feedbackMutation.isPending}
                  className="flex-1"
                  data-testid="input-answer"
                />
                <Button
                  type="submit"
                  disabled={!input.trim() || feedbackMutation.isPending}
                  size="icon"
                  data-testid="button-send-answer"
                >
                  <Send className="w-4 h-4" />
                </Button>
              </form>
            )}

            {sessionQuestions.length > 0 && (
              <div className="text-xs text-muted-foreground text-center">
                {sessionQuestions.length} question{sessionQuestions.length !== 1 ? 's' : ''} completed
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

